const throttleInputButtons=document.getElementById("throttleInput").querySelectorAll("button");function getPlusMinusInputValue(t){const e=t.currentTarget,n=e.parentNode.querySelector(".minus"),u=e.parentNode.querySelector(".plus"),r=e.parentNode.querySelector(".number"),l=e.parentNode.querySelector(".numberValue"),s=parseFloat(l.textContent),o=e.classList.contains("plus")?s+20:s-20;return l.textContent=o,0===o?(n.disabled=!0,r.classList.add("dim"),e.blur()):o>0&&o<100?(n.disabled=!1,u.disabled=!1,r.classList.remove("dim")):100===o&&(u.disabled=!0,e.blur()),o}throttleInputButtons.forEach((t=>{t.addEventListener("click",(t=>{getPlusMinusInputValue(t)}))}));