const throttleInputButtons=document.getElementById("throttleInput").querySelectorAll("button");throttleInputButtons.forEach((t=>{t.addEventListener("click",(t=>{getPlusMinusInputValue(t)}))}));const volumeInputButtons=document.getElementById("volumeInput").querySelectorAll("button");function getPlusMinusInputValue(t){const e=t.currentTarget,n=e.parentNode.querySelector(".minus"),u=e.parentNode.querySelector(".plus"),l=e.parentNode.querySelector(".number"),o=e.parentNode.querySelector(".numberValue"),r=parseFloat(o.textContent),s=e.classList.contains("plus")?r+20:r-20;return o.textContent=s,0===s?(n.disabled=!0,l.classList.add("dim"),e.blur()):s>0&&s<100?(n.disabled=!1,u.disabled=!1,l.classList.remove("dim")):100===s&&(u.disabled=!0,e.blur()),s}volumeInputButtons.forEach((t=>{t.addEventListener("click",(t=>{getPlusMinusInputValue(t)}))}));